const add = (a, b) => a + b;

const sub = (a, b) => a - b;

const multi = (a, b) => a * b;

const area = {
  circle: (r) => (22 / 7) * r ** 2,
  triangle: (b, h) => b * (1 / 2) * h,
  rectangle: multi,
  square: (a) => a ** 2,
};

const quad = (a, b, c) => {
  let x1, x2;

  x1 = (-b + Math.sqrt(b ** 2 - 4 * a * c)) / (2 * a);

  x2 = (-b - Math.sqrt(b ** 2 - 4 * a * c)) / (2 * a);

  return { x1, x2 };
};

module.exports = { add, sub, multi, quad, area };
